import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./styles.css";

function AddCategory() {
  const [newCategory, setNewCategory] = useState("");
  const [categories, setCategories] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingCategory, setEditingCategory] = useState("");

  useEffect(() => {
    const storedCategories = JSON.parse(localStorage.getItem("categories")) || [];
    setCategories(storedCategories);
  }, []);

  const handleAddCategory = () => {
    if (newCategory && !categories.includes(newCategory)) {
      const updatedCategories = [...categories, newCategory];
      setCategories(updatedCategories);
      localStorage.setItem("categories", JSON.stringify(updatedCategories));
      setNewCategory("");
    } else {
      alert("Categoria já existe ou está vazia!");
    }
  };

  const handleDeleteCategory = (index) => {
    const updatedCategories = categories.filter((_, i) => i !== index);
    setCategories(updatedCategories);
    localStorage.setItem("categories", JSON.stringify(updatedCategories));
  };

  const handleEditCategory = (index) => {
    setEditingIndex(index);
    setEditingCategory(categories[index]);
  };

  const handleSaveEdit = (index) => {
    if (!editingCategory) {
      alert("Categoria não pode ser vazia!");
      return;
    }
    const updatedCategories = [...categories];
    updatedCategories[index] = editingCategory;
    setCategories(updatedCategories);
    localStorage.setItem("categories", JSON.stringify(updatedCategories));
    setEditingIndex(null);
    setEditingCategory("");
  };

  return (
    <div className="stock-container">
      <Link to="/Stock" className="btn">Voltar</Link>
      <h2>Gerenciamento de Categorias</h2>

      <div className="add-category-container">
        <input
          type="text"
          placeholder="Nova categoria"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
          className="input-field"
        />
        <button onClick={handleAddCategory} className="btn">Adicionar Categoria</button>
      </div>

      <table className="stock-table">
        <thead>
          <tr>
            <th>Categoria</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((cat, index) => (
            <tr key={index}>
              {editingIndex === index ? (
                <>
                  <td>
                    <input
                      type="text"
                      value={editingCategory}
                      onChange={(e) => setEditingCategory(e.target.value)}
                      className="input-field"
                    />
                  </td>
                  <td>
                    <button onClick={() => handleSaveEdit(index)} className="btn">Salvar</button>
                    <button onClick={() => setEditingIndex(null)} className="btn">Cancelar</button>
                  </td>
                </>
              ) : (
                <>
                  <td>{cat}</td>
                  <td>
                    <button onClick={() => handleEditCategory(index)} className="btn">Editar</button>
                    <button onClick={() => handleDeleteCategory(index)} className="btn">Excluir</button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AddCategory;
